import Link from 'next/link';

const Card = (props) => {

  const { heading, link, text } = props;

  return(
    <div class="w-1/4 font-mono text-lg text-gray-800 text-center border-gray-600 border rounded-lg bg-white
      hover:scale-x-100 shadow hover:bg-black hover:text-white duration-200
    ">
      <Link href={link}>
      <a>
      <h3>{heading}</h3>
        <div class="text-sm">
          {text}
        </div>
     </a>
     </Link>

    </div>
  )
}

export default Card;
